import React, { Fragment, useState } from 'react';
import Formulario from './components/Formulario'
import ListaCitas from './components/ListaCitas'



function App() {

 // 1. Formulario -> Creo Citas

 const [ citas, guardarCitas ] = useState([]) // CoL DeReChA

 const crearCita = cita => {                   // CoL IzQuIeRdA
  guardarCitas([
      ...citas,
      cita
  ]);
}


// 1. ListadoCitas -> Eliminar Cita

const eliminarCita = id => {
  const lasQueQuedanCitas = citas.filter(cita => cita.id !== id);
  guardarCitas(lasQueQuedanCitas)
}


// 2. Cambiar titulo

const titulo = citas.length === 0



  return (
    <Fragment>
      <h1>Administrador de Pacientes</h1>

        <div className="container">
          <div className="row">
            <div className="one-half-column">
              <Formulario 
                crearCita={crearCita}
              />
            </div>
            <div className="one-half-column">
              {citas.map(cita =>
                <ListaCitas 
                  key={cita.id}
                  cita={cita}
                  eliminarCita={eliminarCita}
                />
                )}
            </div>
          </div>
        </div>

    </Fragment>
  );
}

export default App;
